import React from "react";
import MaterialTable from 'material-table';
import Button from '@material-ui/core/Button'
import { useHistory } from "react-router-dom";
import { Search, FirstPage, LastPage, Clear, ChevronLeft, ChevronRight } from "@material-ui/icons"
import HomeIcon from "../components/HomeIcon"
import Menu from "@material-ui/core/Menu"
import MenuItem from "@material-ui/core/MenuItem"

function createJson(username, firstname, lastname, email) {
    return {
        "username":username,
        "firstname":firstname,
        "lastname":lastname,
        "email":email,

    }
}

function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([]);
    const [anchorEl, setAnchorEl] = React.useState(null);

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
      };

    const handleClose = () => {
        setAnchorEl(null);
      };

    return (
        <div style={{textAlign:"center"}}>
            <p>
                {HomeIcon()} 
                <Button 
                    aria-controls="simple-menu" 
                    aria-haspopup="true" 
                    onClick={handleClick}
                    variant="outlined"
                    textTransform="none"
                >
                    Vendor: {currentVendor} 
                </Button>
                <Menu
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                >
                    <MenuItem onClick={() => {
                        handleClose();
                        localStorage.removeItem("token");
                        history.push("/");
                    }}>
                        Log Out</MenuItem>
                </Menu>
            </p>

            <MaterialTable
                title="Verified Users"
                options={{
                        search: true,
                    }}
                icons={{
                    Search: Search,
                    FirstPage: FirstPage,
                    LastPage: LastPage,
                    Clear: Clear,
                    PreviousPage: ChevronLeft,
                    NextPage: ChevronRight
                }}
                columns={[
                    { title: 'Username', field: 'username' },
                    { title: 'First Name', field: 'firstname' },
                    { title: 'Last Name', field: 'lastname' },
                    { title: 'Email', field: 'email' }
                ]}
                data={users.map(u => createJson(u.username, u.firstname, u.lastname, u.email))}
                actions={[
                    {
                      icon: 'save',
                      tooltip: 'Save User',
                      onClick: (event, rowData) => alert(
                          rowData.firstname + " (" + rowData.username + ") claimed at " + currentVendor)
                    }
                  ]}

                  components={{
                    Action: props => (
                      <Button
                        onClick={(event) => props.action.onClick(event, props.data)}
                        color="primary"
                        variant="contained"
                        style={{textTransform: 'none'}}
                        size="medium"
                      >
                        User Claim
                      </Button>
                    ),
                  }}
                />

        </div>
    );
}

export default VendorHomePage;
