import React from "react";
import { useHistory, Link } from "react-router-dom";
import HomeIcon from "../components/HomeIcon"
import TextField from "@material-ui/core/TextField"
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, Jumbotron, Button, Form, FormGroup, Label, Input, InputGroupAddon, InputGroupText, InputGroup } from 'reactstrap'

function HomePage() {
    const history = useHistory();

    const [username, setUsername] = React.useState("");
    const [firstname, setFirstname] = React.useState("");
    const [lastname, setLastName] = React.useState("");
    const [email, setEmail] = React.useState("");    
    const [errorMessage, setErrorMessage] = React.useState("");
    const [collapsed, setCollapsed] = React.useState(true);
    const toggleNavbar = () => setCollapsed(!collapsed);

    function handleSubmit(e) {
        e.preventDefault();

        fetch("http://localhost:5000/user-signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username,
                firstname,
                lastname,
                email,
            })
        })
        .then(res => res.json())
        .then(res => {
            console.log(res);

            if (res.success === false) {
                setErrorMessage(res.message);
            } 
            else {
                setErrorMessage("");
                history.push("/");
            }
        });
    }


    return (
        <div style={{textAlign:"center"}}>



        <Navbar color="faded" light sticky="top">
        <NavbarBrand tag={Link} to={"/"} className="mr-auto" style={{fontSize:"30px"}}>nanovert</NavbarBrand>
        <NavbarToggler onClick={toggleNavbar} className="mr-2" />
        <Collapse isOpen={!collapsed} navbar>
            <Nav navbar>
            <NavItem>
                <NavLink tag={Link} to={"/vendor-login"}>Vendor Login</NavLink>
            </NavItem>
            </Nav>
        </Collapse>
        </Navbar>


        <div>
            <Jumbotron>
                <h1 className="display-3">Get rewards for your Instagram stories!</h1>
                <p className="lead">Nanovert is a new platform connecting students with local businesses. We believe that all people should be rewarded for giving brands exposure and not just “influencers”. The idea is simple… all you have to do is post aesthetic instagram stories at local establishments, with a geo-tag for the business and you’ll be rewarded with money and freebies. </p>
                <br/>
                <hr className="my-2" />
                <br/>
                <p>For businesses, we believe 100 nano-influencers can have a greater impact than one influencer for a fraction of the cost.</p>
                <p className="lead">
                </p>
            </Jumbotron>
        </div>

        <h1 style={{color:"Black"}}>Sign Up</h1>

        <Form style={{marginRight:"35%", marginLeft:"35%"}}>
            <FormGroup>
                <Label for="Username">Username</Label>
                <InputGroup>
                    <InputGroupAddon addonType="prepend">
                        <InputGroupText>@</InputGroupText>
                    </InputGroupAddon>
                    <Input 
                        type="username" 
                        name="username" 
                        placeholder="Instagram Username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                        />
                </InputGroup>
            </FormGroup>

            <FormGroup>
                <Label for="firstname">First Name</Label>
                    <Input 
                        type="firstname" 
                        name="firstname" 
                        placeholder="First Name"
                        value={firstname}
                        onChange={e => setFirstname(e.target.value)}
                        />
            </FormGroup>

            <FormGroup>
                <Label for="lastname">Last Name</Label>
                    <Input 
                        type="lastname" 
                        name="lastname" 
                        placeholder="Last Name"
                        value={lastname}
                        onChange={e => setLastName(e.target.value)}
                        />
            </FormGroup>

            <FormGroup>
                <Label for="Email">Email</Label>
                    <Input 
                        type="Email" 
                        name="Email" 
                        placeholder="Email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        />
            </FormGroup>

            <FormGroup>
                <Label for="Password">Password</Label>
                <Input type="password" name="password" placeholder="Password" />
            </FormGroup>
            <FormGroup check>
                <Label check>
                <Input type="checkbox" />{' '}
                I agree nanovert can share my data with it's partners.
                </Label>
            </FormGroup>

            <br/>

            <Button>Submit</Button>
        </Form>

            <br/>

            <div>
                <p style={{color:"darkslategrey"}}>nanovertuk@gmail.com</p>
                <small style={{color:"darkslategrey", paddingBottom:"20px"}}>Copyright &copy; 2019 - nanovert</small>
            </div>

        </div>
    )    
}

export default HomePage;
