const fetch = require("node-fetch")

function getFollowers(username){
    fetch(`https://www.instagram.com/${req}/?__a=1`)
        .then(function(res) {
            return res.json();
        })
        .then(function(json) {
            console.log(json["graphql"]["user"]["edge_followed_by"]["count"])
            return json["graphql"]["user"]["edge_followed_by"]["count"];
        });
   };

export default getFollowers;
