import React from "react";
import { useHistory, Link } from "react-router-dom";

function VendorHomePage() {
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState(null)
    const token = localStorage.getItem("token");

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    return (
        <div>
            <h2><Link to={`/`}>NANOVERT</Link></h2>  
            <p>Current user: {JSON.stringify(currentVendor)}</p>
            <p>
                <button onClick={() => {
                    localStorage.removeItem("token");
                    history.push("/");
                    }}>
                    Log out.
                </button>
                {users.map(p => {
                    return (
                    <div style={{ borderBottom: "1px solid lightgrey", padding: 40 }}>
                        <p>{p.username}</p>
                        <br />
                    </div>
                    );
                })}
            </p>
            { users }
        </div>
    );
}

export default VendorHomePage;
