import React from "react";
import MaterialTable from "../../node_modules/@material-ui";
import { useHistory, Link } from "react-router-dom";


function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([])
    const [state, setState] = React.useState({
        columns: [
            { title: 'Name', field: 'name' },
            { title: 'Surname', field: 'surname' },
            { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
            {
                title: 'Birth Place',
                field: 'birthCity',
                lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
            },
        ],
        data: [
            { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
            {
                name: 'Zerya Betül',
                surname: 'Baran',
                birthYear: 2017,
                birthCity: 34,
            },
        ],
    });

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    return (
        <div>
            <h2><Link to={`/`}>NANOVERT</Link></h2>  
            <p>Current user: {currentVendor}</p>
            <p>
                <button onClick={() => {
                    localStorage.removeItem("token");
                    history.push("/");
                    }}>
                    Log out.
                </button>
            </p>
            <h3>Users</h3>
            {users.map(u => {
                // const followers = getFollowers(u.username)
                // console.log(followers)
                return (
                    <div>
                        <table style={{ borderBottom: "1px solid lightgrey", width: "80%"}}>
                            <tbody style={{ borderBottom: "1px solid lightgrey"}}>
                                <tr>
                                    <td>{ u.username }</td>
                                    <td>{ u.email }</td>
                                    {/* <td>{ followers }</td> */}
                                </tr>
                            </tbody>
                        </table>
                        < br/>
                    </div>
                );
            })}

            <MaterialTable
                title="Editable Example"
                columns={state.columns}
                data={state.data}
                editable={{
                    onRowAdd: newData =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        setState(prevState => {
                            const data = [...prevState.data];
                            data.push(newData);
                            return { ...prevState, data };
                        });
                        }, 600);
                    }),
                    onRowUpdate: (newData, oldData) =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        if (oldData) {
                            setState(prevState => {
                            const data = [...prevState.data];
                            data[data.indexOf(oldData)] = newData;
                            return { ...prevState, data };
                            });
                        }
                        }, 600);
                    }),
                    onRowDelete: oldData =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        setState(prevState => {
                            const data = [...prevState.data];
                            data.splice(data.indexOf(oldData), 1);
                            return { ...prevState, data };
                        });
                        }, 600);
                    }),
                }}
                />

        </div>
    );
}

export default VendorHomePage;
