import React from "react";
import MaterialTable from 'material-table';
import { useHistory, Link } from "react-router-dom";

function createJson(username, firstname, lastname, email) {
    return {
        "username":username,
        "firstname":firstname,
        "lastname":lastname,
        "email":email,

    }
}

function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([])
    const [state, setState] = React.useState({
        columns: [
            { title: 'Username', field: 'username' },
            { title: 'First Name', field: 'firstname' },
            { title: 'Last Name', field: 'lastname' },
            { title: 'Email', field: 'email' },
        ],
        data: users
    });

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    return (
        <div>
            <h2><Link to={`/`}>NANOVERT</Link></h2>  
            <p>Current user: {currentVendor}</p>
            <p>
                <button onClick={() => {
                    localStorage.removeItem("token");
                    history.push("/");
                    }}>
                    Log out.
                </button>
            </p>
            <h3>Users</h3>
            {users.map(u => {
                return (
                    <div>
                        <table style={{ borderBottom: "1px solid lightgrey", width: "80%"}}>
                            <tbody style={{ borderBottom: "1px solid lightgrey"}}>
                                <tr>
                                    <td>{ u.username }</td>
                                    <td>{ u.email }</td>
                                    {/* <td>{ followers }</td> */}
                                </tr>
                            </tbody>
                        </table>
                        < br/>
                    </div>
                );
            })}

            <MaterialTable
                title="Verified Users"
                columns={state.columns}
                data={state.data}
                editable={{
                    onRowAdd: newData =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        setState(prevState => {
                            const data = [...prevState.data];
                            data.push(newData);
                            return { ...prevState, data };
                        });
                        }, 600);
                    }),
                    onRowUpdate: (newData, oldData) =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        if (oldData) {
                            setState(prevState => {
                            const data = [...prevState.data];
                            data[data.indexOf(oldData)] = newData;
                            return { ...prevState, data };
                            });
                        }
                        }, 600);
                    }),
                    onRowDelete: oldData =>
                    new Promise(resolve => {
                        setTimeout(() => {
                        resolve();
                        setState(prevState => {
                            const data = [...prevState.data];
                            data.splice(data.indexOf(oldData), 1);
                            return { ...prevState, data };
                        });
                        }, 600);
                    }),
                }}
                />

        </div>
    );
}

export default VendorHomePage;
