import React from "react";
import MaterialTable from 'material-table';
import { useHistory, Link } from "react-router-dom";

function createJson(username, firstname, lastname, email) {
    return {
        "username":username,
        "firstname":firstname,
        "lastname":lastname,
        "email":email,

    }
}

function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([])
    const [state, setState] = React.useState([]);

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    return (
        <div>
            <h2><Link to={`/`}>NANOVERT</Link></h2>  
            <p>Current user: {currentVendor}</p>
            <p>
                <button onClick={() => {
                    localStorage.removeItem("token");
                    history.push("/");
                    }}>
                    Log out.
                </button>
            </p>

            <MaterialTable
                title="Verified Users"
                columns={[
                    { title: 'Username', field: 'username' },
                    { title: 'First Name', field: 'firstname' },
                    { title: 'Last Name', field: 'lastname' },
                    { title: 'Email', field: 'email' }
                ]}
                data={users.map(u => createJson(u.username, u.firstname, u.lastname, u.email))}
                actions={[
                    {
                      icon: 'save',
                      tooltip: 'Save User',
                      onClick: (event, rowData) => alert("You saved " + rowData.name)
                    }
                  ]}
                  components={{
                    Action: props => (
                      <Button
                        onClick={(event) => props.action.onClick(event, props.data)}
                        color="primary"
                        variant="contained"
                        style={{textTransform: 'none'}}
                        size="small"
                      >
                        My Button
                      </Button>
                    ),
                  }}
                />

        </div>
    );
}

export default VendorHomePage;

class ActionOverriding extends React.Component {
    render() {
      return (
        <MaterialTable
          title="Action Overriding Preview"
          columns={[
            { title: 'Name', field: 'name' },
            { title: 'Surname', field: 'surname' },
            { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
            {
              title: 'Birth Place',
              field: 'birthCity',
              lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
            },
          ]}
          data={[
            { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
            { name: 'Zerya Betül', surname: 'Baran', birthYear: 2017, birthCity: 34 },
          ]}
          actions={[
            {
              icon: 'save',
              tooltip: 'Save User',
              onClick: (event, rowData) => alert("You saved " + rowData.name)
            }
          ]}
          components={{
            Action: props => (
              <Button
                onClick={(event) => props.action.onClick(event, props.data)}
                color="primary"
                variant="contained"
                style={{textTransform: 'none'}}
                size="small"
              >
                My Button
              </Button>
            ),
          }}
        />
      )
    }
  }
