import React from "react";
import { useHistory, Link } from "react-router-dom";
import getFollowers from "../functions/getFollowers"


function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([])

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    return (
        <div>
            <h2><Link to={`/`}>NANOVERT</Link></h2>  
            <p>Current user: {currentVendor}</p>
            <p>
                <button onClick={() => {
                    localStorage.removeItem("token");
                    history.push("/");
                    }}>
                    Log out.
                </button>
            </p>
            <h3>Users</h3>
            {users.map(u => {
                const followers = getFollowers(u.username)
                return (
                    <div>
                        <table style={{ borderBottom: "1px solid lightgrey", width: "80%"}}>
                            <tr>
                                <td>{ u.username }</td>
                                <td>{ u.email }</td>
                                <td>{ followers }</td>
                            </tr>
                        </table>
                        < br/>
                    </div>
                );
            })}
        </div>
    );
}

export default VendorHomePage;
