import React from "react";
import { useHistory, Link } from "react-router-dom";
import HomeIcon from "../components/HomeIcon"
import TextField from "@material-ui/core/TextField"
import Button from "@material-ui/core/Button"

function HomePage() {
    const history = useHistory();

    const [username, setUsername] = React.useState("");
    const [firstname, setFirstname] = React.useState("");
    const [lastname, setLastName] = React.useState("");
    const [email, setEmail] = React.useState("");    
    const [errorMessage, setErrorMessage] = React.useState("");

    function handleSubmit(e) {
        e.preventDefault();

        fetch("http://localhost:5000/user-signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username,
                firstname,
                lastname,
                email,
            })
        })
        .then(res => res.json())
        .then(res => {
            console.log(res);

            if (res.success === false) {
                setErrorMessage(res.message);
            } 
            else {
                setErrorMessage("");
                history.push("/");
            }
        });
    }

    return (
        <div style={{textAlign:"center"}}>
            <div style={{
                backgroundColor:"#009688",
                position:"fixed",
                width:"100%",
                height:"60px"
                }}>
                <div style={{
                    display:"inline-block",
                    float:"left",
                    marginLeft:"30px"}}
                    >
                        {HomeIcon()}
                    </div>

                <Link 
                    to={"/vendor-login"} 
                    style={{
                        float:"right",
                        marginRight:"30px",
                        marginTop:"10px",
                        color:"white",
                        fontSize:30,
                        textDecoration:"none",
                        fontWeight:"normal",

                        }}>
                            Log In (Vendor)
                </Link>
            </div>

            <div>
                <br/>
                <p style={{
                    fontSize:60, 
                    marginLeft:"10%", 
                    marginRight:"10%", 
                    fontStyle:"bold"
                    }}>
                        GET REWARDS FOR YOUR INSTAGRAM STORIES!
                    </p>
            </div>

            <h1 style={{color:"Black"}}>Tell Me More...</h1>

            <div style={{
                backgroundColor:"whitesmoke",
                marginLeft:"10%",
                marginRight:"10%",
                borderRadius:"20px",
                paddingTop:"20px",
                paddingBottom:"20px"
            }}>
                <p style={{marginLeft:"10%", marginRight:"10%", color:"darkslategrey"}}>Nanovert is a new platform connecting students with local businesses. We believe that all people should be rewarded for giving brands exposure and not just “influencers”. The idea is simple… all you have to do is post aesthetic instagram stories at local establishments, with a geo-tag for the business and you’ll be rewarded with money and freebies. </p>
                <p style={{marginLeft:"10%", marginRight:"10%", color:"darkslategrey"}}>For businesses, we believe 100 nano-influencers can have a greater impact than one influencer for a fraction of the cost.</p>
            </div>

            <h1 style={{color:"Black"}}>Sign Up</h1>

            <div style={{
                backgroundColor:"whitesmoke",
                marginLeft:"10%",
                marginRight:"10%",
                borderRadius:"10px",
                paddingTop:"20px"
            }}>
                <form onSubmit={handleSubmit}>
                    <TextField 
                        style={{backgroundColor:"white"}}
                        label="Username" 
                        variant="outlined"
                        value={username}
                        onChange={e => setUsername(e.target.value)} />
                    <br />
                    <br />
                    <TextField
                        style={{backgroundColor:"white"}}
                        label="First Name" 
                        variant="outlined"
                        placeholder="First Name"
                        value={firstname}
                        onChange={e => setFirstname(e.target.value)}
                    />
                    <br />
                    <br />
                    <TextField
                        style={{backgroundColor:"white"}}
                        label="Last Name" 
                        variant="outlined"
                        placeholder="Last Name"
                        value={lastname}
                        onChange={e => setLastName(e.target.value)}
                    />
                    <br />
                    <br />
                    <TextField
                        style={{backgroundColor:"white"}}
                        label="Email" 
                        variant="outlined"
                        placeholder="Email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                    <p style={{fontWeight:"light", color:"grey", fontSize:12}}>By signing up you agree that nanovert can distribute this data to any of its partners.</p>
                    <Button variant="contained" style={{marginBottom:"5%"}}>Sign Up</Button>
                    {errorMessage}
                </form>
            </div>

            <br/>
            <br/>

            <div>
                <p style={{color:"darkslategrey"}}>nanovertuk@gmail.com</p>
                <small style={{color:"darkslategrey", paddingBottom:"20px"}}>Copyright &copy; 2019 - nanovert</small>
            </div>

        </div>
    )    
}

export default HomePage;
