import React from "react";
import MaterialTable from 'material-table';
import { Fab } from '@material-ui/core'
import { useHistory, Link } from "react-router-dom";
import { Search, FirstPage, LastPage, Clear, ChevronLeft, ChevronRight } from "@material-ui/icons"
import HomeIcon from "../components/HomeIcon"
import Menu from "@material-ui/core/Menu"
import MenuItem from "@material-ui/core/MenuItem"
import MenuIcon from "@material-ui/icons/Menu"
import { TableSortLabel, IconButton } from "@material-ui/core/"
import RedeemIcon from "@material-ui/icons/Redeem"
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, Jumbotron, Button, Form, FormGroup, Label, Input, InputGroupAddon, InputGroupText, InputGroup } from 'reactstrap'


function createJson(username, firstname, lastname, email) {
    return {
        "username":username,
        "firstname":firstname,
        "lastname":lastname,
        "email":email,

    }
}

function VendorHomePage() {
    const token = localStorage.getItem("token");
    const history = useHistory();
    const [currentVendor, setCurrentVendor] = React.useState(null);
    const [users, setUsers] = React.useState([]);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [collapsed, setCollapsed] = React.useState(true);
    const toggleNavbar = () => setCollapsed(!collapsed);

    React.useEffect(() => {
        fetch("http://localhost:5000/vendor-home")
            .then(res => res.json())
            .then(u => { setUsers(u)});
    }, []);

    React.useEffect(() => {
        if (token) {
            fetch("http://localhost:5000/get-current-vendor", {
                headers: {"Authorization": `Bearer ${token}`}
            })
            .then(res => res.json())
            .then(u => { setCurrentVendor(u['username']) });
        }
    }, [token]);

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
      };

    const handleClose = () => {
        setAnchorEl(null);
      };

    return (
        <div style={{textAlign:"center", backgroundColor:"whitesmoke"}}>


            <Navbar color="faded" light sticky="top">
                <NavbarBrand tag={Link} to={"/"} className="mr-auto" style={{fontSize:"30px"}}>nanovert</NavbarBrand>
                {currentVendor}
                <NavbarToggler onClick={toggleNavbar} className="mr-2" />
                <Collapse isOpen={!collapsed} navbar>
                    <Nav navbar>
                    <NavItem>
                        <NavLink tag={Link} onClick={() => {
                            localStorage.removeItem("token")
                            history.push("/")}}>Logout
                        </NavLink>
                    </NavItem>
                    </Nav>
                </Collapse>
            </Navbar>
            {/* <div style={{backgroundColor:"#009688", height:"60px"}}>
                <div style={{float:"left", marginLeft:"20px"}}>{HomeIcon()}</div>

                <div style={{float:"right"}}> 
                <b style={{paddingRight:"10px", color:"white"}}>{currentVendor}</b>
                    <Button 
                        aria-controls="simple-menu" 
                        aria-haspopup="true" 
                        onClick={handleClick}
                        variant="outlined"
                        style={{textTransform:"none", color:"white", marginRight:"20px", marginTop:"15px"}}
                    >
                        <MenuIcon />
                    </Button>
                    <Menu
                        id="simple-menu"
                        anchorEl={anchorEl}
                        keepMounted
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={() => {
                            handleClose();
                            localStorage.removeItem("token");
                            history.push("/");
                        }}>
                            Log Out</MenuItem>
                    </Menu>
                </div>
            </div> */}

            <MaterialTable
                style={{margin:"5%", backgroundColor:"white"}}
                title="Verified Users"
                options={{
                        search: true,
                        defaultExpanded:"true"
                    }}
                icons={{
                    Search: Search,
                    FirstPage: FirstPage,
                    LastPage: LastPage,
                    PreviousPage: ChevronLeft,
                    NextPage: ChevronRight,
                    SortArrow: TableSortLabel,
                    Clear:Clear
                }}
                columns={[
                    { title: 'Username', field: 'username' },
                    { title: 'First Name', field: 'firstname' },
                    { title: 'Last Name', field: 'lastname' },
                    { title: 'Email', field: 'email' }
                ]}
                data={users.map(u => createJson(u.username, u.firstname, u.lastname, u.email))}
                actions={[
                    {
                      icon: 'save',
                      tooltip: 'Save User',
                      onClick: (event, rowData) => alert(
                          rowData.firstname + " (" + rowData.username + ") claimed at " + currentVendor)
                    }
                  ]}
                  components={{
                    Action: props => (
                      <Fab
                        onClick={(event) => props.action.onClick(event, props.data)}
                        color="primary"
                        variant="contained"
                        style={{textTransform: 'none'}}
                        size="medium"
                        disableElevation="true"
                      >
                        <RedeemIcon />
                      </Fab>
                    ),
                  }}
                />

        </div>
    );
}

export default VendorHomePage;
