import React from "react";
import { useHistory, Link } from "react-router-dom";
import TextField from "@material-ui/core/TextField"
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, Button } from 'reactstrap'


function VendorLoginPage() {
    const history = useHistory();

    const [oldPassword, setOldPassword] = React.useState("");
    const [newPassword, setNewPassword] = React.useState("");
    const [errorMessage, setErrorMessage] = React.useState("");
    const [collapsed, setCollapsed] = React.useState(true);
    const [vendorname, setVendorname] = React.useState("");
    const toggleNavbar = () => setCollapsed(!collapsed);


    function handleSubmit(vendorname, password) {
        fetch("http://localhost:5000/vendor-change-password", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                vendorname,
                oldPassword,
                newPassword
            })
        })
        .then(res => res.json())
        .then(res => {
            console.log(res);

            if (res.success === false) {
                setErrorMessage(res.message);
            } 
            else {
                setErrorMessage("");
            }
        });
    }

    return (
        <div style={{textAlign:"center"}}>
            <Navbar color="faded" light sticky="top">
            <NavbarBrand tag={Link} to={"/"} className="mr-auto" style={{fontSize:"30px"}}>nanovert</NavbarBrand>
            <NavbarToggler onClick={toggleNavbar} className="mr-2" />
            <Collapse isOpen={!collapsed} navbar>
                <Nav navbar>
                <NavItem>
                    <NavLink tag={Link} to={"/vendor-login"}>Vendor Login</NavLink>
                </NavItem>
                </Nav>
            </Collapse>
            </Navbar>

            <h3>Update Password</h3>

            <form onSubmit={handleSubmit}>
                <br />
                <TextField
                    label="Username" 
                    variant="outlined"
                    placeholder="Username"
                    value={vendorname}
                    onChange={e => setVendorname(e.target.value)}
                />
                <br />
                <br />
                <TextField
                    label="Old Password" 
                    variant="outlined"
                    placeholder="Old Password"
                    value={oldPassword}
                    onChange={e => setOldPassword(e.target.value)}
                />
                <br />
                <br />
                <TextField
                    label="New Password" 
                    variant="outlined"
                    placeholder="New Password"
                    value={newPassword}
                    onChange={e => setNewPassword(e.target.value)}
                />
                <br />
                <br />
                <Button variant="contained" type="submit">Log In</Button>
                <br />
                {errorMessage}
            </form>
        </div>
    );
    }


export default VendorLoginPage;
