import React from "react";
import { useHistory, Link } from "react-router-dom";
import HomeIcon from "../components/HomeIcon"
import TextField from "@material-ui/core/TextField"
import Button from "@material-ui/core/Button"
import Navbar from "react-bootstrap/Navbar"
import Nav from "react-bootstrap/Nav"

function HomePage() {
    const history = useHistory();

    const [username, setUsername] = React.useState("");
    const [firstname, setFirstname] = React.useState("");
    const [lastname, setLastName] = React.useState("");
    const [email, setEmail] = React.useState("");    
    const [errorMessage, setErrorMessage] = React.useState("");

    function handleSubmit(e) {
        e.preventDefault();

        fetch("http://localhost:5000/user-signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username,
                firstname,
                lastname,
                email,
            })
        })
        .then(res => res.json())
        .then(res => {
            console.log(res);

            if (res.success === false) {
                setErrorMessage(res.message);
            } 
            else {
                setErrorMessage("");
                history.push("/");
            }
        });
    }

    return (
        <div style={{textAlign:"center"}}>
            {HomeIcon()}<Link to={"/vendor-login"} style={{marginLeft:"80%"}}>Vendor Login</Link>


            <p style={{fontSize:60, marginLeft:"20%", marginRight:"20%", fontStyle:"bold"}}>GET REWARDS FOR YOUR INSTAGRAM STORIES</p>

            <h2 style={{color:"Blue"}}>Tell Me More...</h2>
            <p style={{marginLeft:"20%", marginRight:"20%"}}>Nanovert is a new platform connecting students with local businesses. We believe that all people should be rewarded for giving brands exposure and not just “influencers”. The idea is simple… all you have to do is post aesthetic instagram stories at local establishments, with a geo-tag for the business and you’ll be rewarded with money and freebies. </p>
            <p>For businesses, we believe 100 nano-influencers can have a greater impact than one influencer for a fraction of the cost.</p>

            <h3 style={{fontWeight:"normal", fontSize: 25}}>Sign Up</h3>
            <form onSubmit={handleSubmit}>
                <TextField 
                    label="Username" 
                    variant="outlined"
                    value={username}
                    onChange={e => setUsername(e.target.value)} />
                <br />
                <br />
                <TextField
                    label="First Name" 
                    variant="outlined"
                    placeholder="First Name"
                    value={firstname}
                    onChange={e => setFirstname(e.target.value)}
                />
                <br />
                <br />
                <TextField
                    label="Last Name" 
                    variant="outlined"
                    placeholder="Last Name"
                    value={lastname}
                    onChange={e => setLastName(e.target.value)}
                />
                <br />
                <br />
                <TextField
                    label="Email" 
                    variant="outlined"
                    placeholder="Email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                />
                <br />
                <br />
                <Button variant="contained">Sign Up</Button>
                {errorMessage}
            </form>
            <br />
            <small>By signing up you agree that nanovert can distribute this data to any partners of nanovert</small>
            <br/>
            <br/>
            <br/>
        </div>
    )    
}

export default HomePage;
