import React from "react";
import { useParams, Link } from "react-router-dom";

function OfferPage() {
    const { id } = useParams();
    const [offer, setOffer] = React.useState(null);

    React.useEffect(() => {
        fetch(`http://localhost:5000/post/${id}`)
        .then(res => res.json())
        .then(p => {
            setOffer(p);
        });
    }, []);

    if (offer === null) {
        return <div>Loading offer...</div>;
    }

    if (offer.error) {
        return <div>Error: {offer.error}</div>;
    }

    return (
        <div>
        <h2><Link to={`/`}>Home</Link></h2>
        <h1>This is the offer page</h1>

        <div style={{color: 'white', background: "darkslategrey", padding: 40 }}>
            {offer.title}
            <br />
            <small>
            <p>{offer.vendor}</p>
            <p>Discount: {offer.discount}</p>
            <p>Remaining: {offer.numavailable}</p>
            </small>
        </div>
        </div>
    );
}

export default OfferPage;
