const fetch = require("node-fetch");

function getFollowers(username){
    return (
        fetch(`https://www.instagram.com/${username}/?__a=1`)
            .then(function(res) {
            return res.json();
        })
            .then(function(json) {
            return json["graphql"]["user"]["edge_followed_by"]["count"];
        }));
};

