import React from "react";
import { useHistory, Link } from "react-router-dom";
import TextField from "@material-ui/core/TextField"
import { Collapse, Navbar, NavbarToggler, NavbarBrand, Nav, NavItem, NavLink, Jumbotron, Button, Form, FormGroup, Label, Input, InputGroupAddon, InputGroupText, InputGroup } from 'reactstrap'


function VendorLoginPage() {
    const history = useHistory();

    const [username, setUsername] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [errorMessage, setErrorMessage] = React.useState("");
    const [collapsed, setCollapsed] = React.useState(true);
    const toggleNavbar = () => setCollapsed(!collapsed);

    function handleSubmit(e) {
        e.preventDefault();

        fetch("http://localhost:5000/vendor-login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username,
                password
            })
        })
        .then(res => res.json())
        .then(res => {
            console.log(res);

            if (res.success === false) {
            setErrorMessage(res.message);
            } else {
            setErrorMessage("");
            localStorage.setItem("token", res.token);
            history.push("/vendor-home");
            }
        });
    }

    return (
        <div style={{textAlign:"center"}}>
            <Navbar color="faded" light sticky="top">
            <NavbarBrand tag={Link} to={"/"} className="mr-auto" style={{fontSize:"30px"}}>nanovert</NavbarBrand>
            </Navbar>

            <h3>Log In</h3>

            <form onSubmit={handleSubmit}>
                <br />
                <TextField
                    label="Username" 
                    variant="outlined"
                    placeholder="username"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                />
                <br />
                <br />
                <TextField
                    label="Password" 
                    variant="outlined"
                    placeholder="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                />
                <br />
                <br />
                <Button variant="contained" type="submit">Log In</Button>
                <br />
                {errorMessage}
            </form>
        </div>
    );
    }


export default VendorLoginPage;
